var btnOpen = document.querySelector(".page-header__logo-menu-image--open");

var closeClass = document.querySelector(".page-header__logo-mobile_menu-close");

var mobileMenuSpan = document.querySelector(".page-header__logo-mobile_menu span");

var mobilemenu = document.querySelector(".page-header__menu-main");

var pageHeaderLogoMobileMenu = document.querySelector(".page-header__logo-mobile_menu");

if (document.body.clientWidth<768) {
  mobilemenu.classList.add("page-header__menu-main--hidden");

  btnOpen.classList.add("page-header__element-active");

  mobileMenuSpan.classList.add("page-header__element-active");
}

  window.onload = function() {
    btnOpen.onclick = function () {
      mobilemenu.classList.toggle("page-header__menu-main--hidden");
      this.classList.toggle("page-header__logo-mobile_menu-close");
      if (mobileMenuSpan.contains(closeClass)) {
        mobileMenuSpan.classList.remove("page-header__logo-menu-image--open");
      }
    }
  };

var programsProgramLinks = document.querySelectorAll(".programs__program-link");
var arrows = document.querySelectorAll(".programs__icon-right-arrow-long");
var contentLinks = document.querySelectorAll(".programs__program-main-content-link");

for (var i = 0; i<contentLinks.length; i++) {
  for (var z = 0; z<arrows.length; z++) {
    contentLinks[i].onmouseover = function () {
      arrows[i].src = "img/icon-right-arrow-verylong.svg";
    }
    contentLinks[i].onmouseout = function () {
      arrows[i].src = "img/icon-right-arrow-long.svg";
    }
  }
}
